import { createStore, applyMiddleware } from 'redux';
import reducer from './reducer';
import { fetchTodosData, fetchContactsData, setLoginState, deleteTodoAction, addTodoAction, completeTodoAction } from './actions'
import axios from 'axios';
import { Alert } from 'react'

const thunkMiddleware = require('redux-thunk').default

let TODO_ID=0;

//fetching todos from server
const fetchTodos = () => {
    return function(dispatch){
    axios.get('http://localhost:8000/todos')
    .then(response =>{
        dispatch(fetchTodosData(response.data))
    })
    .catch(error => {
        console.log('Unable to fetch todos!')
    })

    }
}

//fetching contacts from server
const fetchContacts = () => {
    return function(dispatch){
    axios.get('http://localhost:8000/contacts')
    .then(response =>{
        dispatch(fetchContactsData(response.data))
    })
    .catch(error => {
        console.log('Unable to fetch contacts!')
    })

    }
}

export function Login(email,password){
    return function(dispatch){
    axios.post('http://localhost:8000/auth/login',
        {
            email: email,
            password: password
        })
        .then(response =>{
            dispatch(setLoginState(response.data.authorized))
        })
        .catch(error =>{
            Alert.alert('Login Failed', 'Email or Password is incorrect');
        });
    }
}

export function deleteTodo(id){
    return function(dispatch){
    axios.delete('http://localhost:8000/todos/'+id)
        .then(response =>{
            dispatch(deleteTodoAction(id))
        })
        .catch(error =>{
            Alert.alert('Unable to delete!');
        });
    }
}

export function addTodo(desc){
    return function(dispatch){
    axios.post('http://localhost:8000/todos',{
        'id': ++TODO_ID,
        'text': desc,
        'complete': false
        })
        .then(response =>{
            dispatch(addTodoAction({
        'id': TODO_ID,
        'text': desc,
        'complete': false
        }))
        })
        .catch(error =>{
            Alert.alert('Unable to add!');
        });
    }
}

export function completeTodo(id){
    return function(dispatch){
    axios.put('http://localhost:8000/todos/'+id,{
        'complete': true
        })
        .then(response =>{
            dispatch(completeTodoAction(id))
        })
        .catch(error =>{
            Alert.alert('Unable to update!');
        });
    }
}




export const store = createStore(reducer, applyMiddleware(thunkMiddleware));
store.dispatch(fetchTodos())
store.dispatch(fetchContacts())
