import React from 'react';
import { useDispatch } from 'react-redux'
import { Login } from '../redux/actions'

class LoginForm extends React.Component {
    constructor(props){
        super(props);
        this.authHandler = this.authHandler.bind(this);
        this.state = { error_message: '' }
    }

    authHandler(e){
        e.preventDefault();

        const dispatch = useDispatch();
        dispatch(Login(this.email.value, this.password.value));
        this.setState({error_message: 'Wrong username or password!'})
        return
    }

    render(){
    return (
    <div>
        <form>
          <input type="email" name="email" ref={(c) => this.email = c} placeholder="email" />
          <input type="password" name="password" ref={(c) => this.password = c} placeholder="password" />
          <input onClick={this.authHandler} type="submit" value="Log In" />
        </form>
           <p ref={(c) => this.error = c}>{this.state.error_message}</p>
    </div>
    )
    }
}

export default LoginForm;
