import * as actions from './actionTypes'

let TODO_ID=0

var state = {
                "inputText": "", "status": "all", "authorized": false, "todos": [],
                "filteredTodos": [], "contacts": [{"id":1, "name":"contact-1", "number": "+92 3-- 21-----"}]
}

export default function reducer(state = state, action){
    if(action.type === actions.ADD_TODO){
        return {
            ...state,
            todos: [...state.todos, {text: action.payload.text, complete: false, id: ++TODO_ID}]
        };
    }
    else if (action.type === actions.DELETE_TODO){
        return {
        ...state,
        todos: state.todos.filter(item => item.id !== action.payload.id)
        };
    }
    return state;
}
