import axios from 'axios';

let TODO_ID=0;

var initial_state = {
                    "authorized": false,
                    "todos": [],
                    "contacts": []
}

export default function reducer(state = initial_state, action){

    if(action.type === 'fetchTodoData'){
        return {
            ...state,
            todos: action.payload.todos
        };
    }
    else if(action.type === 'fetchContactsData'){
        return {
            ...state,
            contacts: action.payload.contacts
        };
    }
    else if(action.type === 'login'){
        let auth = false;
        axios.post('http://localhost:8000/auth/login',
        {
          email: action.payload.email,
          password: action.payload.password
        }).then(res =>{
            auth = res.data.authorized;
            console.log(res.data.authorized);
        });

        return {
                    ...state,
                    authorized: auth
                };
    }
    else if(action.type === 'addTodos'){
        return {
            ...state,
            todos: [...state.todos, {text: action.payload.text, complete: false, id: ++TODO_ID}]
        };
    }
    else if(action.type === 'completeTodos'){
        return {
                ...state,
                todos: state.todos.map((item) => {
                if (item.id === action.payload.id)
                {
                    return {
                    ...item, complete: !item.complete,
                    };

                }
                return item;
            })
            };
    }
    else if (action.type === 'deleteTodo'){
        return {
        ...state,
        todos: state.todos.filter(item => item.id !== action.payload.id)
        };
    }
    return state;
}
