import * as actions from './actionTypes'

let TODO_ID=0

let initial_state = { "inputText": "", "status": "all", "authorized": false, "todos": [],"filteredTodos": [], "contacts": [{"id":1, "name":"contact-1", "number": "+92 3-- 21-----"}]}
export default function reducer(state = initial_state, action){
    if(action.type === actions.AddTodo){
        return {
            ...state,
            todos: [...state.todos, {text: action.payload.text, complete: false, id: ++TODO_ID}]
        };
    }
    else if (action.type === actions.DeleteTodo){
        return {
        ...state,
        todos: todos.filter(item => item.id !== action.payload.id)
        };
    }
    return state;
}
