import React from 'react';
import AddForm from './AddForm'
import TodoList from './TodoList'

function Dashboard({setInputText, inputText, todos, setTodos, setStatus, filteredTodos}){
    var menu_option = ""
    function changeMenu(e){
        menu_option = e.target.value
    }
    function dashboardBody(){
        console.log(menu_option == "Todos")
        if (menu_option == "Todos"){
            return (
                <div>
                < AddForm
                        setInputText = {setInputText}
                        inputText = {inputText}
                        todos = {todos}
                        setTodos = {setTodos}
                        setStatus = {setStatus}
                    />
                    <TodoList
                        todos = {todos}
                        setTodos = {setTodos}
                        filteredTodos = {filteredTodos}
                    />
                </div>
            )
        }
        return <div> nothing</div>
    }
    return (
        <div>
            <center>
            <input onClick={changeMenu} type="submit" value="Contacts" />
            <input onClick={changeMenu} type="submit" value="Todos" />
            </center>
            <hr />
            <div ></div>
        </div>
    )
}

export default Dashboard;
